import React from "react";
import classes from "./Dialogs.module.css";
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {
  sendMessageCreator,
  updateNewMessageBodyCreator,
} from "../../redux/state";

function Dialogs(props) {
  let state = props.store.getState().dialogs;

  let dialogsElements = state.dialogs.dialogsData.map((d) => (
    <DialogItem name={d.name} id={d.id} />
  ));

  let messagesElements = state.dialogs.messagesData.map((m) => (
    <Message text={m.text} />
  ));

  // let newMessageBody = state.newMessageBody;

  // let onSendMessageClick = () => {
  //   props.store.dispatch(sendMessageCreator());
  // };

  // let onNewMessageChange = (event) => {
  //   let body = event.target.value;
  //   props.store.dispatch(updateNewMessageBodyCreator(body));
  // };

  return (
    <div className={classes.dialogs}>
      {/* <div className={classes.dialogsItems}>{dialogsElements}</div> */}
      {/* <div className={classes.messages}>{messagesElements}</div> */}
      <div className={classes.chat}>
        <div className={classes.chatWindow}></div>
        <div className={classes.chatFoot}>
          <div className={classes.chatTextarea}>
            <textarea
              // value={newMessageBody}
              // onChange={onNewMessageChange}
              placeholder="Enter your message"
            />
          </div>
          <div className={classes.chatButton}>
            {/* <button onClick={onSendMessageClick}>Send</button> */}
          </div>
        </div>
      </div>
    </div>
  );
}
export default Dialogs;
