import React from "react";
import { newMessageActionCreator } from "../../../redux/state";
import Message from "../Message/Message";
import classes from "./../Dialogs.module.css";

function Chat(props) {
  let newMessage = React.createRef();

  let addMessage = () => {
    props.dispatch(newMessageTextActionCreator());
  };
  let onMesChange = () => {
    let text = newMessage.current.value;
    let action = newMessageActionCreator(text);
    props.dispatch(action);
  };
  return (
    <div className={classes.chat}>
      <div className={classes.chatWindow}>
        <Message text={props.newMessage} />
      </div>
      <div className={classes.chatFoot}>
        <textarea onChange={onMesChange} ref={newMessage} />
        <button onClick={addMessage}>Send</button>
      </div>
    </div>
  );
}

export default Chat;
